import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import GoogleMaps from '../components/GoogleMaps';

const locations = [
  { name: 'Davis Cleveland', latitude: 37.7749, longitude: -122.4194 },
  { name: 'Location 2', latitude: 37.7749, longitude: -122.4194 },
  { name: 'Location 3', latitude: 37.7749, longitude: -122.4194 },
];

const D_SearchBar = () => {
  const [progress, setProgress] = useState(0);
  const [showComponent, setShowComponent] = useState(false);
  const [targetLocation, setTargetLocation] = useState({ name: '', latitude: 0, longitude: 0 });

  useEffect(() => {
    // Randomly select a location from the locations array
    const randomIndex = Math.floor(Math.random() * locations.length);
    setTargetLocation(locations[randomIndex]);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prevProgress) => {
        const newProgress = prevProgress + 20; // Adjust the increment as desired
        return newProgress >= 100 ? 100 : newProgress;
      });
    }, 500); // Adjust the interval (in milliseconds) to control the speed of progress

    return () => {
      clearInterval(interval);
    };
  }, []);

  useEffect(() => {
    if (progress === 100) {
      // Progress has completed, show the desired component
      setShowComponent(true);
    }
  }, [progress]);

  return (
    <div>
      <div className="d-flex justify-content-center align-items-center bg-danger text-white text-bold" style={{ height: '7vh' }}>
        <h1>Target: Davis Cleveland</h1>
      </div>
      <div className="progress">
        <div
          className="progress-bar bg-success"
          role="progressbar"
          style={{ width: `${progress}%` }}
          aria-valuenow={progress}
          aria-valuemin="0"
          aria-valuemax="100"
        >
          {progress === 100 ? "Completed" : "Locating..."} {/* Display "Completed" when progress is 100, otherwise show "Locating..." */}
        </div>
      </div>
      {showComponent ? <h3>{targetLocation.name}</h3> : null} {/* Use null as the fallback */}
      {showComponent ? <GoogleMaps className = "bg-secondary" latitude={targetLocation.latitude} longitude={targetLocation.longitude} /> : null} {/* Use null as the fallback */}
    </div>
  );
};

export default D_SearchBar;
