import React, { useState, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import GoogleMaps from '../components/GoogleMaps';

const SearchBar = () => {
  const [progress, setProgress] = useState(0);
  const [showComponent, setShowComponent] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress((prevProgress) => {
        const newProgress = prevProgress + 5; // Adjust the increment as desired
        return newProgress >= 100 ? 100 : newProgress;
      });
    }, 500); // Adjust the interval (in milliseconds) to control the speed of progress

    return () => {
      clearInterval(interval);
    };
  }, []);

  useEffect(() => {f
    if (progress === 100) {
      // Progress has completed, show the desired component
      setShowComponent(true);
    }
  }, [progress]);

  return (
    <div>
      <div className="d-flex justify-content-center align-items-center bg-danger text-white text-bold" style={{ height: '6vh' }}>
        <h1>Target: Sydney Tiet</h1>
      </div>
      <div className="progress">
        <div
          className="progress-bar bg-success"
          role="progressbar"
          style={{ width: `${progress}%` }}
          aria-valuenow={progress}
          aria-valuemin="0"
          aria-valuemax="100"
        >
          {progress === 100 ? "Completed" : "Locating..."} {/* Display "Completed" when progress is 100, otherwise show "Locating..." */}
        </div>
      </div>
      {showComponent ? <GoogleMapsSection /> : <CreateRest />}
    </div>
  );
};

export default SearchBar;
