import React from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '60%', // Use percentage value to make it responsive
  height: '80vh', // Use a fixed height or percentage value to control the height
};

const mapWrapperStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh',
  background: 'lightgray',
};

const Maps = ({ latitude, longitude }) => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY", // Replace with your API key
  });

  const center = { lat: latitude, lng: longitude };

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null);
  }, []);

  return isLoaded ? (
    <div style={mapWrapperStyle}>
      <div className='bg-danger' style={{ margin: 'auto' }}>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={8}
          mapTypeId='satellite'
          onUnmount={onUnmount}
        >
          <Marker position={center} icon={{ url: "https://maps.google.com/mapfiles/ms/icons/red-dot.png" }} />
        </GoogleMap>
      </div>
    </div>
  ) : <></>;
};

export default React.memo(Maps);
